<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bubble Sort Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        .bar {
            display: inline-block;
            width: 20px;
            margin: 1px;
            background-color: teal;
        }
        #arrayContainer {
            display: flex;
            justify-content: center;
            align-items: flex-end;
            height: 300px;
            border: 1px solid #ccc;
            margin-bottom: 20px;
        }
        .sorted {
            background-color: orange;
        }
    </style>
</head>
<body>

<h1>Bubble Sort Visualization</h1>
<button id="sortButton">Start Bubble Sort</button>
<div id="arrayContainer"></div>

<script>
    const arrayContainer = document.getElementById('arrayContainer');
    const sortButton = document.getElementById('sortButton');

    // Generate a random array of numbers
    function generateArray(size) {
        const array = [];
        for (let i = 0; i < size; i++) {
            array.push(Math.floor(Math.random() * 100) + 1);
        }
        return array;
    }

    // Render the array as bars
    function renderArray(array) {
        arrayContainer.innerHTML = '';
        array.forEach(value => {
            const bar = document.createElement('div');
            bar.style.height = value * 2 + 'px';
            bar.classList.add('bar');
            arrayContainer.appendChild(bar);
        });
    }

    // Bubble sort algorithm
    async function bubbleSort(array) {
        const n = array.length;
        for (let i = 0; i < n - 1; i++) {
            for (let j = 0; j < n - i - 1; j++) {
                // Highlight active bars
                const bars = document.querySelectorAll('.bar');
                bars[j].classList.add('sorted');
                bars[j + 1].classList.add('sorted');

                if (array[j] > array[j + 1]) {
                    // Swap
                    [array[j], array[j + 1]] = [array[j + 1], array[j]];
                }

                renderArray(array);
                await new Promise(resolve => setTimeout(resolve, 100)); // Delay for visualization

                // Reset bar styles
                bars[j].classList.remove('sorted');
                bars[j + 1].classList.remove('sorted');
            }
        }
    }

    // Start the sorting process on button click
    sortButton.addEventListener('click', () => {
        const array = generateArray(30);
        renderArray(array);
        bubbleSort(array);
    });

    // Initial render
    const initialArray = generateArray(30);
    renderArray(initialArray);
</script>

</body>
</html>
