<body>

    <h1>Bubble Sort Visualization</h1>

    <div class="container">
        <div id="controls">
            <input type="text" id="arrayInput" placeholder="Enter comma-separated numbers (e.g., 5,3,8,4,2)">
            <button id="generateBtn">Random Array</button>
            <button id="sortBtn">Sort</button>
        </div>
        <div id="visualization-container"></div>
         <div id="status-container">
            <div id="status">Enter an array or generate a random one to begin.</div>
            <div id="array-display"></div>
        </div>
    </div>

    <div class="container">
        <h2>Pseudocode</h2>
        <div id="pseudocode">
            <span data-line="1">procedure bubbleSort( A : list of sortable items )</span>
            <span data-line="2">  n = length(A)</span>
            <span data-line="3">  repeat</span>
            <span data-line="4">    swapped = false</span>
            <span data-line="5">    for i = 1 to n-1 inclusive do</span>
            <span data-line="6">      /* if this pair is out of order */</span>
            <span data-line="7">      if A[i-1] > A[i] then</span>
            <span data-line="8">        /* swap them and remember something changed */</span>
            <span data-line="9">        swap( A[i-1], A[i] )</span>
            <span data-line="10">       swapped = true</span>
            <span data-line="11">     end if</span>
            <span data-line="12">   end for</span>
            <span data-line="13">   n = n - 1</span>
            <span data-line="14"> until not swapped</span>
            <span data-line="15">end procedure</span>
        </div>
    </div>


    <script>
        const arrayInput = document.getElementById('arrayInput');
        const generateBtn = document.getElementById('generateBtn');
        const sortBtn = document.getElementById('sortBtn');
        const visualizationContainer = document.getElementById('visualization-container');
        const statusEl = document.getElementById('status');
        const arrayDisplayEl = document.getElementById('array-display');
        const pseudocodeLines = document.querySelectorAll('#pseudocode span');

        let array = [];
        const ANIMATION_SPEED_MS = 300;

        // --- Utility Functions ---

        function sleep(ms) {
            return new Promise(resolve => setTimeout(resolve, ms));
        }

        function disableControls() {
            sortBtn.disabled = true;
            generateBtn.disabled = true;
            arrayInput.disabled = true;
        }

        function enableControls() {
            sortBtn.disabled = false;
            generateBtn.disabled = false;
            arrayInput.disabled = false;
        }

        function highlightPseudocodeLine(lineNumber) {
            pseudocodeLines.forEach(line => line.classList.remove('highlight-line'));
            if (lineNumber > 0) {
                const lineEl = document.querySelector(`#pseudocode span[data-line="${lineNumber}"]`);
                if(lineEl) lineEl.classList.add('highlight-line');
            }
        }

        // --- Core Logic ---

        function generateRandomArray() {
            const newArray = [];
            const size = Math.floor(Math.random() * 10) + 10; // Size between 10 and 19
            for (let i = 0; i < size; i++) {
                newArray.push(Math.floor(Math.random() * 91) + 10); // Numbers between 10 and 100
            }
            arrayInput.value = newArray.join(',');
            parseInput();
        }

        function parseInput() {
            const inputText = arrayInput.value.trim();
            if (inputText === '') {
                array = [];
            } else {
                array = inputText.split(',')
                    .map(item => parseInt(item.trim(), 10))
                    .filter(num => !isNaN(num) && num > 0);
            }
            renderArray();
            statusEl.textContent = 'Ready to sort.';
            enableControls();
        }

        function renderArray(comparingIndices = [], swappingIndices = [], sortedIndex = array.length) {
            visualizationContainer.innerHTML = '';
            const maxValue = Math.max(...array, 1); // Avoid division by zero for empty array

            array.forEach((value, index) => {
                const bar = document.createElement('div');
                bar.classList.add('bar');
                const barHeight = (value / maxValue) * 100;
                bar.style.height = `${barHeight}%`;
                bar.textContent = value;

                if (index >= sortedIndex) {
                    bar.classList.add('sorted');
                } else if (swappingIndices.includes(index)) {
                    bar.classList.add('swapping');
                } else if (comparingIndices.includes(index)) {
                    bar.classList.add('comparing');
                }

                visualizationContainer.appendChild(bar);
            });

            arrayDisplayEl.textContent = `[${array.join(', ')}]`;
        }

        async function bubbleSort() {
            disableControls();
            statusEl.textContent = 'Sorting...';
            let n = array.length;
            let swapped;

            highlightPseudocodeLine(1);
            await sleep(ANIMATION_SPEED_MS * 2);

            highlightPseudocodeLine(2);
            await sleep(ANIMATION_SPEED_MS);

            do {
                swapped = false;
                highlightPseudocodeLine(3);
                await sleep(ANIMATION_SPEED_MS);

                highlightPseudocodeLine(4);
                await sleep(ANIMATION_SPEED_MS);

                highlightPseudocodeLine(5);
                await sleep(ANIMATION_SPEED_MS);

                for (let i = 0; i < n - 1; i++) {
                    // Highlight elements being compared
                    renderArray([i, i + 1], [], n);
                    statusEl.textContent = `Comparing ${array[i]} and ${array[i+1]}`;
                    highlightPseudocodeLine(7);
                    await sleep(ANIMATION_SPEED_MS);

                    if (array[i] > array[i + 1]) {
                        // Highlight elements being swapped
                        swapped = true;
                        highlightPseudocodeLine(9);
                        statusEl.textContent = `Swapping ${array[i]} and ${array[i+1]}`;
                        renderArray([], [i, i + 1], n);
                        await sleep(ANIMATION_SPEED_MS);

                        // Perform the swap
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];

                        // Re-render to show the result of the swap
                        renderArray([i, i + 1], [], n);
                        highlightPseudocodeLine(10);
                        await sleep(ANIMATION_SPEED_MS);
                    }
                }
                n--; // The last element of the unsorted part is now sorted
                renderArray([], [], n); // Mark the newly sorted element
                highlightPseudocodeLine(13);
                await sleep(ANIMATION_SPEED_MS * 2);

            } while (swapped);

            highlightPseudocodeLine(14);
            await sleep(ANIMATION_SPEED_MS);

            statusEl.textContent = 'Array is sorted!';
            renderArray([], [], 0); // Mark all as sorted
            highlightPseudocodeLine(15);
            await sleep(ANIMATION_SPEED_MS * 2);
            highlightPseudocodeLine(0); // Clear highlight
            enableControls();
        }

        // --- Event Listeners ---
        window.addEventListener('load', generateRandomArray);
        generateBtn.addEventListener('click', generateRandomArray);
        sortBtn.addEventListener('click', () => {
            parseInput(); // Ensure the array is up-to-date
            if(array.length > 1) {
                bubbleSort();
            } else {
                statusEl.textContent = "Array is too small to sort.";
            }
        });
        arrayInput.addEventListener('change', parseInput);

    </script>
</body>
</html>
