            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            background-color: #f0f0f0;
        }

        .container {
            width: 100%;
            max-width: 600px;
            padding: 24px;
            background: white;
            border-radius: 8px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        h1 {
            text-align: center;
        }

        .input-area {
            display: flex;
            justify-content: space-between;
            margin-bottom: 24px;
        }

        input {
            flex-grow: 1;
            padding: 10px;
            margin-right: 16px;
            border: 1px solid #ccc;
            border-radius: 4px;
        }

        button {
            padding: 10px 20px;
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
        }

        .linked-list {
            display: flex;
            justify-content: center;
