    <div class="container">
        <h1>Introduction to Linked Lists</h1>
        <div class="input-area">
            <input type="text" id="nodeValue" placeholder="Enter node value" />
            <button id="addNodeBtn">Add Node</button>
        </div>
        <div class="linked-list" id="linkedList"></div>
    </div>

    <script>
        const nodeValueInput = document.getElementById("nodeValue");
        const addNodeBtn = document.getElementById("addNodeBtn");
        const linkedListContainer = document.getElementById("linkedList");

        function createNode(value) {
            const node = document.createElement("div");
            node.textContent = value;
            node.className = "node";
            node.onclick = function() {
                removeNode(node);
            };

            linkedListContainer.appendChild(node);
            node.style.transform = "translateX(-50px)";
            setTimeout(() => {
                node.style.transform = "translateX(0)";
            }, 10); // Allow time for the layout to update
        }

        function removeNode(node) {
            node.classList.add("fade-out");
            setTimeout(() => {
                linkedListContainer.removeChild(node);
            }, 500); // Matches the CSS transition time
