            display: grid;
            grid-template-columns: repeat(5, 1fr);
            gap: 16px;
            margin-top: 24px;
        }
        .bucket {
            width: 80px;
            height: 80px;
            border: 2px solid #333;
            background-color: white;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: background-color 0.3s ease;
        }
        .highlight {
            background-color: #ffc107;
        }
    </style>
</head>
<body>
    <h1>Understanding Hash Tables</h1>
    <div class="form-container">
        <input type="text" id="key" placeholder="Key" />
        <input type="text" id="value" placeholder="Value" />
        <button onclick="addEntry()">Add</button>
        <button onclick="retrieveEntry()">Retrieve</button>
        <button onclick="deleteEntry()">Delete</button>
    </div>
    <div class="hash-table" id="hashTable">
        <!-- Hash table buckets will be created here. -->
    </div>

    <script>
        const hashTableSize = 5;
        const hashTable = Array(hashTableSize).fill(null).map(() => []);
        const hashTableDiv = document.getElementById('hashTable');

        // Initialize hash table UI
        function createHashTableUI() {
            for (let i = 0; i < hashTableSize; i++) {
                const bucketDiv = document.createElement('div');
                bucketDiv.className = 'bucket';
                bucketDiv.id = 'bucket-' + i;
                bucketDiv.innerText = '';
                hashTableDiv.appendChild(bucketDiv);
            }
        }

        // Simple hash function
        function hash(key) {
            return key.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0) % hashTableSize;
        }

        function addEntry() {
            const key = document.getElementById('key').value;
            const value = document.getElementById('value').value;
            const index = hash(key);

            // Handle collision resolution with chaining
            hashTable[index].push({ key, value });
            updateBucket(index);

            alert(`${key} added.`);
        }

        function retrieveEntry() {
            const key = document.getElementById('key').value;
            const index = hash(key);
            const bucket = hashTable[index];

            const entry = bucket.find(item => item.key === key);
            if (entry) {
                highlightBucket(index);
                alert(`Value for ${key} is ${entry.value}`);
            } else {
                alert(`Key ${key} not found.`);
            }
        }

        function deleteEntry() {
            const key = document.getElementById('key').value;
            const index = hash(key);
            const bucket = hashTable[index];

            const entryIndex = bucket.findIndex(item => item.key === key);
            if (entryIndex > -1) {
                bucket.splice(entryIndex, 1);
                updateBucket(index);
                alert(`${key} deleted.`);
            } else {
                alert(`Key ${key} not found.`);
            }
        }

        function updateBucket(index) {
            const bucketDiv = document.getElementById('bucket-' + index);
            bucketDiv.innerText = hashTable[index].map(item => `${item.key}: ${item.value}`).join('\n');
        }

        function highlightBucket(index) {
            const bucketDiv = document.getElementById('bucket-' + index);
            bucketDiv.classList.add('highlight');
            setTimeout(() => bucketDiv.classList.remove('highlight'), 1000);
        }

        // Create the initial hash table UI
        createHashTableUI();
    </script>
</body>
</html>
```

