            display: flex;
            justify-content: center;
            gap: 16px;
        }
        input[type="number"] {
            width: 100px;
        }
        button {
            padding: 10px 20px;
            background-color: #007BFF;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
        }
        button:hover {
            background-color: #0056b3;
        }
    </style>
</head>
<body>

    <h1>Understanding Weighted Graphs</h1>
    <h2>Learning Objective: Understand how to represent a weighted graph and how weights affect paths</h2>

    <canvas id="graphCanvas" width="600" height="400"></canvas>

    <div class="controls">
        <input type="number" id="nodeCount" placeholder="Nodes" min="2" max="10">
        <button id="createGraph">Create Graph</button>
    </div>

    <script>
        const canvas = document.getElementById("graphCanvas");
        const ctx = canvas.getContext("2d");
        let graph = [];
        let edgeWeights = {};

        // Function to draw the graph
        function drawGraph() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            graph.forEach(node => {
                ctx.fillStyle = "black";
                ctx.beginPath();
                ctx.arc(node.x, node.y, 20, 0, Math.PI * 2);
                ctx.fill();
                ctx.closePath();
                ctx.fillStyle = "white";
                ctx.fillText(node.label, node.x - 5, node.y + 5);
            });

            for (let [edge, weight] of Object.entries(edgeWeights)) {
                const [from, to] = edge.split(",");
                ctx.strokeStyle = "gray";
                ctx.beginPath();
                ctx.moveTo(graph[from].x, graph[from].y);
                ctx.lineTo(graph[to].x, graph[to].y);
                ctx.stroke();
                ctx.fillStyle = "red";
                ctx.fillText(weight, (graph[from].x + graph[to].x) / 2, (graph[from].y + graph[to].y) / 2);
            }
        }

        function createGraph() {
            const numNodes = parseInt(document.getElementById("nodeCount").value);
            graph = [];
            edgeWeights = {};
            const angleStep = (Math.PI * 2) / numNodes;

            for (let i = 0; i < numNodes; i++) {
                graph.push({
                    x: 300 + 200 * Math.cos(angleStep * i),
                    y: 200 + 200 * Math.sin(angleStep * i),
                    label: i
                });
            }

            // Randomly create edges with weights
            for (let i = 0; i < numNodes; i++) {
                for (let j = i + 1; j < numNodes; j++) {
                    if (Math.random() < 0.5) { // Randomly connect nodes
                        const weight = Math.floor(Math.random() * 10) + 1; // Weight between 1 and 10
                        edgeWeights[`${i},${j}`] = weight;
                        edgeWeights[`${j},${i}`] = weight; // undirected edge
                    }
                }
            }

            drawGraph();
        }

        document.getElementById("createGraph").onclick = createGraph;
    </script>
</body>
</html>
```

### Explanation of the Design

