                node.style.left = `${Math.min(Math.max(x, 0), targetRect.width - 40)}px`;
                node.style.top = `${Math.min(Math.max(y, 0), targetRect.height - 40)}px`;
                positionNodes();
            });

            return node;
        }

        function positionNodes() {
            // Simply positions nodes manually for this demo; optimize as needed.
            let spacing = 80;
            nodes.forEach((node, index) => {
                let posX = (index % 5) * spacing;
                let posY = Math.floor(index / 5) * spacing;
                node.style.left = `${posX}px`;
                node.style.top = `${posY}px`;
            });
        }
