<body>

    <h1>Understanding Stacks</h1>
    <div class="stack" id="stack"></div>
    <div class="input-section">
        <input type="number" id="inputValue" placeholder="Enter a number">
        <button id="pushButton">Push</button>
        <button id="popButton">Pop</button>
    </div>
    <div class="error" id="errorMessage"></div>

    <script>
        const stack = [];
        const stackEl = document.getElementById('stack');
        const inputValue = document.getElementById('inputValue');
        const errorMessage = document.getElementById('errorMessage');

        document.getElementById('pushButton').addEventListener('click', function () {
            const value = inputValue.value;
            if (!value) {
                errorMessage.textContent = 'Please enter a valid number.';
                return;
            }

            errorMessage.textContent = '';
            stack.push(value);

            const stackItem = document.createElement('div');
            stackItem.className = 'stack-item';
            stackItem.textContent = value;
            stackItem.style.transform = 'translateY(-200%)';
            stackEl.appendChild(stackItem);
            requestAnimationFrame(() => {
                stackItem.style.transform = 'translateY(0)';
            });

            inputValue.value = '';
        });

        document.getElementById('popButton').addEventListener('click', function () {
            if (stack.length === 0) {
                errorMessage.textContent = 'Stack is empty!';
                return;
            }
            errorMessage.textContent = '';
            const itemToRemove = stack.pop();
            const items = document.querySelectorAll('.stack-item');
            if (items.length > 0) {
                const lastItem = items[items.length - 1];
                lastItem.style.opacity = '0';
                setTimeout(() => {
                    stackEl.removeChild(lastItem);
                }, 300);
            }
        });
    </script>

</body>
</html>
```

