### JavaScript Functionality (script.js)

```javascript
class TreeNode {
    constructor(value) {
        this.value = value;
        this.left = null;
        this.right = null;
    }
}

class BST {
    constructor() {
        this.root = null;
    }

    insert(value) {
        const newNode = new TreeNode(value);
        if (!this.root) {
            this.root = newNode;
            return;
        }
        this.insertNode(this.root, newNode);
    }

    insertNode(node, newNode) {
        if (newNode.value < node.value) {
            if (!node.left) {
                node.left = newNode;
            } else {
                this.insertNode(node.left, newNode);
            }
        } else {
            if (!node.right) {
                node.right = newNode;
            } else {
                this.insertNode(node.right, newNode);
            }
        }
    }

    clear() {
        this.root = null;
    }
}

const bst = new BST();
const visualization = document.getElementById('bst-visualization');
const insertButton = document.getElementById('insert-button');
const clearButton = document.getElementById('clear-button');
const numberInput = document.getElementById('number-input');

insertButton.onclick = () => {
    const value = parseInt(numberInput.value);
    if (!isNaN(value)) {
        bst.insert(value);
        drawTree(bst.root);
        numberInput.value = '';
    }
};

clearButton.onclick = () => {
    bst.clear();
    visualization.innerHTML = '';
};

function drawTree(node) {
    const width = 500;
    const height = 300;
    visualization.innerHTML = ''; // Clear previous visualization
    drawNode(node, width / 2, 20, width / 4, visualization);
}

function drawNode(node, x, y, offsetX, container) {
    if (node) {
        const element = document.createElement('div');
        element.innerText = node.value;
        element.style.position = 'absolute';
        element.style.left = `${x}px`;
        element.style.top = `${y}px`;
        element.style.padding = '8px';
        element.style.background = '#4CAF50';
        element.style.color = 'white';
        element.style.borderRadius = '4px';
        element.style.transition = '0.5s';
        container.appendChild(element);

        // Draw left and right child
        drawNode(node.left, x - offsetX, y + 60, offsetX / 2, container);
        drawNode(node.right, x + offsetX, y + 60, offsetX / 2, container);
    }
}
```

