        function updateDequeVisual() {
            const dequeContainer = document.getElementById("deque");
            dequeContainer.innerHTML = '';
            deque.forEach((item, index) => {
                const box = document.createElement("div");
                box.className = "box";
                box.textContent = item;
                if (index === 0) {
                    box.style.transform = 'scale(1.1)'; // Highlight first item (front)
                }
                dequeContainer.appendChild(box);
            });
        }

        function addToFront() {
            const value = document.getElementById("inputValue").value;
            if (value) {
                deque.unshift(value);
                updateDequeVisual();
                document.getElementById("inputValue").value = '';
            }
        }

        function addToBack() {
            const value = document.getElementById("inputValue").value;
            if (value) {
                deque.push(value);
                updateDequeVisual();
                document.getElementById("inputValue").value = '';
            }
        }

        function removeFromFront() {
            if (deque.length > 0) {
                deque.shift();
                updateDequeVisual();
            }
        }

        function removeFromBack() {
            if (deque.length > 0) {
                deque.pop();
                updateDequeVisual();
            }
        }
    </script>

