            background-color: #ffffff;
            margin: 16px 0;
            padding: 8px;
        }
        .queue-element {
            width: 30px;
            height: 50px;
            background-color: #007bff;
            color: white;
            display: flex;
            align-items: center;
            justify-content: center;
            margin: 0 8px;
            border-radius: 5px;
            transition: all 0.5s;
        }
        .button {
            padding: 10px 20px;
            background-color: #28a745;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            margin: 0 8px;
        }
        .button:hover {
            background-color: #218838;
        }
        #feedback {
            text-align: center;
            margin-top: 16px;
        }
        input {
            padding: 10px;
            margin-right: 8px;
            border: 2px solid #007bff;
            border-radius: 5px;
        }
    </style>
</head>
<body>

    <h1>Understanding Queues: FIFO Data Structure</h1>

    <div id="queue"></div>

    <div style="text-align: center;">
        <input type="number" id="enqueueInput" placeholder="Enter a number">
        <button class="button" id="enqueueButton">Enqueue</button>
        <button class="button" id="dequeueButton">Dequeue</button>
    </div>

    <div id="feedback"></div>

    <script>
        const queue = [];
        const queueDisplay = document.getElementById('queue');
        const feedback = document.getElementById('feedback');

        document.getElementById('enqueueButton').addEventListener('click', function() {
            const input = document.getElementById('enqueueInput');
            const value = input.value;
            if (value) {
                queue.push(value);
                const newElement = document.createElement('div');
                newElement.classList.add('queue-element');
                newElement.innerText = value;
                queueDisplay.appendChild(newElement);
                feedback.innerText = `Added: ${value}`;
                input.value = '';
            } else {
                feedback.innerText = 'Please enter a number.';
            }
        });

        document.getElementById('dequeueButton').addEventListener('click', function() {
            if (queue.length > 0) {
                const removedElement = queue.shift();
                const firstElement = queueDisplay.firstElementChild;
                firstElement.style.transform = 'translateX(-100%)';
                setTimeout(() => {
                    queueDisplay.removeChild(firstElement);
                    feedback.innerText = `Removed: ${removedElement}`;
                }, 500);
            } else {
                feedback.innerText = 'Queue is empty.';
            }
        });
    </script>
</body>
</html>
