            text-align: center;
            font-size: 1.2em;
        }
        #bst-input {
            text-align: center;
            margin-bottom: 16px;
        }
        #bst-input input {
            padding: 10px;
            font-size: 1em;
        }
        #bst-input button {
            padding: 10px 15px;
            font-size: 1em;
            margin-left: 8px;
            cursor: pointer;
        }
        #bst-visualization {
            display: flex;
            justify-content: center;
            margin-top: 20px;
        }
        .node {
            width: 40px;
            height: 40px;
            background-color: #007bff;
            color: white;
            border-radius: 20px;
            display: flex;
            align-items: center;
            justify-content: center;
            position: relative;
            transition: transform 0.3s ease;
        }
        .node:hover {
            transform: scale(1.1);
        }
        .line {
            position: absolute;
            width: 2px;
            background-color: #333;
            transition: height 0.3s ease;
        }
    </style>
</head>
<body>
    <h1>Binary Search Tree Interactive Exploration</h1>
    <div id="learning-objective">Understand how Binary Search Trees organize data and how insertion works.</div>
    <div id="bst-input">
        <input type="number" id="inputValue" placeholder="Enter a number">
        <button onclick="insertNode()">Insert Node</button>
        <button onclick="resetTree()">Reset Tree</button>
    </div>
    <div id="bst-visualization"></div>
    <script>
        class BSTNode {
            constructor(value) {
                this.value = value;
                this.left = null;
                this.right = null;
            }
        }

        class BST {
            constructor() {
                this.root = null;
            }

            insert(value) {
                const newNode = new BSTNode(value);
                if (this.root === null) {
                    this.root = newNode;
                } else {
                    this._insertNode(this.root, newNode);
                }
                this.drawTree();
            }

            _insertNode(node, newNode) {
                if (newNode.value < node.value) {
                    if (node.left === null) {
                        node.left = newNode;
                    } else {
                        this._insertNode(node.left, newNode);
                    }
                } else {
                    if (node.right === null) {
                        node.right = newNode;
                    } else {
                        this._insertNode(node.right, newNode);
                    }
                }
            }

            drawTree() {
                const container = document.getElementById('bst-visualization');
                container.innerHTML = '';
                this._drawNode(this.root, container, 300, 40, 120);
            }

            _drawNode(node, container, x, y, offsetX) {
                if (node) {
                    const nodeDiv = document.createElement('div');
                    nodeDiv.className = 'node';
                    nodeDiv.innerText = node.value;
                    nodeDiv.style.left = `${x}px`;
                    nodeDiv.style.top = `${y}px`;
                    container.appendChild(nodeDiv);

                    if (node.left) {
                        this._drawLine(container, x, y, x - offsetX, y + 60);
                        this._drawNode(node.left, container, x - offsetX, y + 60, offsetX / 2);
                    }
                    if (node.right) {
                        this._drawLine(container, x, y, x + offsetX, y + 60);
                        this._drawNode(node.right, container, x + offsetX, y + 60, offsetX / 2);
                    }
                }
            }

            _drawLine(container, x1, y1, x2, y2) {
                const line = document.createElement('div');
                line.className = 'line';
                const length = Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
                line.style.height = `${length}px`;
                const angle = Math.atan2(y2 - y1, x2 - x1) * 180 / Math.PI;
                line.style.transform = `translate(${x1}px, ${y1}px) rotate(${angle}deg)`;
                container.appendChild(line);
            }
        }

        const bst = new BST();

        function insertNode() {
            const inputValue = document.getElementById('inputValue');
            const value = parseInt(inputValue.value);
            if (!isNaN(value)) {
                bst.insert(value);
                inputValue.value = '';
            }
        }

        function resetTree() {
            bst.root = null;
            bst.drawTree();
        }
    </script>
</body>
</html>
```

