            <input type="number" id="inputSize" value="5" min="1" max="10" />
            <button id="generateArray">Generate Random Array</button>
            <button id="sortArray">Sort Array</button>
        </div>
    </div>

    <script>
        const visualization = document.getElementById('visualization');
        const inputSize = document.getElementById('inputSize');
        const generateButton = document.getElementById('generateArray');
        const sortButton = document.getElementById('sortArray');

        // Function to generate a random array of given size
        function generateRandomArray(size) {
            visualization.innerHTML = '';
            const array = Array.from({ length: size }, () => Math.floor(Math.random() * 100));
            array.forEach(value => {
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = `${value}px`;
                bar.dataset.value = value; // store the value for sorting
                visualization.appendChild(bar);
            });
            return array;
        }

        // Insertion Sort Implementation
        async function insertionSort(array) {
            const bars = document.querySelectorAll('.bar');
            for (let i = 1; i < array.length; i++) {
                let currentValue = array[i];
                let j = i - 1;

                // Move elements greater than currentValue to one position ahead of their current position
                while (j >= 0 && array[j] > currentValue) {
                    array[j + 1] = array[j];
                    bars[j + 1].style.height = `${array[j]}px`; // update the bar height
                    bars[j + 1].classList.remove('sorted'); // remove sorted class from this bar
                    await new Promise(resolve => setTimeout(resolve, 500)); // delay for animation
                    j--;
                }
                array[j + 1] = currentValue;
                bars[j + 1].style.height = `${currentValue}px`; // update bar
                bars[j + 1].classList.add('sorted'); // mark this bar as sorted
            }
        }

        // Event Listeners
        generateButton.addEventListener('click', () => {
            const size = parseInt(inputSize.value);
            generateRandomArray(size);
        });

        sortButton.addEventListener('click', async () => {
            const bars = document.querySelectorAll('.bar');
            const array = Array.from(bars).map(bar => parseInt(bar.dataset.value));
            await insertionSort(array);
        });

        // Initial array generation
        generateRandomArray(5); // generate a default array on load
    </script>
</body>
</html>
