                const bar = document.createElement("div");
                bar.className = "bar";
                bar.style.height = `${value * 2}px`;
                arrayContainer.appendChild(bar);
            }
        }

        async function bubbleSort() {
            const bars = document.querySelectorAll('.bar');
            const arr = [...array]; // Keep the original array intact

            for (let i = 0; i < arr.length - 1; i++) {
                for (let j = 0; j < arr.length - 1 - i; j++) {
                    bars[j].style.backgroundColor = "#ffc107"; // Highlight current pair
                    bars[j + 1].style.backgroundColor = "#ffc107";

                    if (arr[j] > arr[j + 1]) {
                        // Swap values
                        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                        // Update bar heights
                        bars[j].style.height = `${arr[j] * 2}px`;
                        bars[j + 1].style.height = `${arr[j + 1] * 2}px`;
                    }

                    await new Promise(resolve => setTimeout(resolve, 500)); // Pause for visual effect
                    bars[j].style.backgroundColor = "#007bff"; // Reset color
                    bars[j + 1].style.backgroundColor = "#007bff";
                }
            }
        }

