        }
        .bar {
            margin: 0 8px;
            background-color: #007bff;
            display: inline-block;
            transition: height 0.5s, background-color 0.5s;
        }
        .pivot {
            background-color: #ff5722;
        }
        .sorted {
            background-color: #4caf50;
        }
        #controls {
            display: flex;
            justify-content: center;
            margin-top: 16px;
        }
        button {
            padding: 10px 20px;
            margin: 0 16px;
            cursor: pointer;
            border: none;
            background-color: #007bff;
            color: white;
            font-size: 16px;
            border-radius: 5px;
            transition: background-color 0.3s;
        }
        button:hover {
            background-color: #0056b3;
        }
        #arraySize {
            max-width: 200px;
            text-align: center;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Interactive Quick Sort</h1>
        <h2>Visualize the Quick Sort Algorithm</h2>
        <div id="array"></div>
        <div id="controls">
            <input type="number" id="arraySize" min="3" max="20" value="10">
            <button id="generateArray">Generate Array</button>
            <button id="sortArray">Sort Array</button>
        </div>
    </div>

    <script>
        const arrayContainer = document.getElementById("array");
        const arraySizeInput = document.getElementById("arraySize");
        const generateButton = document.getElementById("generateArray");
        const sortButton = document.getElementById("sortArray");

        let array = [];

        function generateRandomArray(size) {
            array = Array.from({length: size}, () => Math.floor(Math.random() * 100) + 1);
            drawArray();
        }

        function drawArray() {
            arrayContainer.innerHTML = "";
            array.forEach(value => {
                const bar = document.createElement("div");
                bar.className = "bar";
                bar.style.height = `${value * 2}px`;
                bar.style.width = "30px";
                arrayContainer.appendChild(bar);
            });
        }

        function swap(a, b) {
            const temp = array[a];
            array[a] = array[b];
            array[b] = temp;
            drawArray();
        }

        async function quickSort(low, high) {
            if (low < high) {
                const pivotIndex = await partition(low, high);
                await quickSort(low, pivotIndex - 1);
                await quickSort(pivotIndex + 1, high);
            }
        }

        async function partition(low, high) {
            const pivot = array[high];
            let i = low - 1;
            for (let j = low; j < high; j++) {
                highlightPivot(j, high);
                await new Promise(resolve => setTimeout(resolve, 500));
                if (array[j] < pivot) {
                    i++;
                    swap(i, j);
                }
                resetPivot(j, high);
            }
            swap(i + 1, high);
            return i + 1;
        }

        function highlightPivot(j, high) {
            const bars = document.querySelectorAll(".bar");
            bars[j].classList.add("pivot");
            bars[high].classList.add("pivot");
        }

        function resetPivot(j, high) {
            const bars = document.querySelectorAll(".bar");
            bars[j].classList.remove("pivot");
            bars[high].classList.remove("pivot");
        }

        generateButton.addEventListener("click", () => {
            const size = parseInt(arraySizeInput.value, 10);
            generateRandomArray(size);
        });

        sortButton.addEventListener("click", async () => {
            await quickSort(0, array.length - 1);
            drawArray();
        });

        // Generate a default array on page load
        generateRandomArray(10);
    </script>
</body>
</html>
```

