            statusText.textContent = "Array generated! Click 'Sort Array' to start sorting.";
        };

        const renderArray = () => {
            arrayContainer.innerHTML = '';
            array.forEach(number => {
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = `${number}px`;
                bar.textContent = number;
                arrayContainer.appendChild(bar);
            });
        };

        const bubbleSort = async () => {
            const len = array.length;
            statusText.textContent = "Sorting in progress...";
            for (let i = 0; i < len - 1; i++) {
                for (let j = 0; j < len - 1 - i; j++) {
                    const bars = document.querySelectorAll('.bar');
                    bars[j].style.backgroundColor = 'red';
                    bars[j + 1].style.backgroundColor = 'red';

                    await new Promise(resolve => setTimeout(resolve, 500)); // Delay for animation

                    if (array[j] > array[j + 1]) {
                        [array[j], array[j + 1]] = [array[j + 1], array[j]]; // Swap
                    }

                    bars[j].style.height = `${array[j]}px`;
                    bars[j + 1].style.height = `${array[j + 1]}px`;
                    bars[j].style.backgroundColor = 'green';
                    bars[j + 1].style.backgroundColor = 'green';
                }
            }
            statusText.textContent = "Sorting complete!";
            renderArray();
        };

        document.getElementById('generate').addEventListener('click', createArray);
        document.getElementById('sort').addEventListener('click', bubbleSort);

        // Initialize the array on page load
        createArray();
    </script>
</body>
</html>
```

