    <script>
        const arrayContainer = document.getElementById('array-container');
        const sortButton = document.getElementById('sort-button');
        const resetButton = document.getElementById('reset-button');
        const info = document.getElementById('info');

        let array = generateRandomArray(10, 100);
        drawArray(array);

        sortButton.addEventListener('click', async () => {
            // Disable buttons during sorting
            sortButton.disabled = true;
            resetButton.disabled = true;
            await quickSort(array, 0, array.length - 1);
            info.textContent = "Sorting Complete!";
            sortButton.disabled = false;
            resetButton.disabled = false;
        });

        resetButton.addEventListener('click', () => {
            array = generateRandomArray(10, 100);
            drawArray(array);
            info.textContent = "Click 'Start Quick Sort' to visualize the sorting process.";
        });

        function generateRandomArray(size, max) {
            return Array.from({ length: size }, () => Math.floor(Math.random() * max) + 1);
        }

        function drawArray(arr) {
            arrayContainer.innerHTML = '';
            arr.forEach(value => {
                const bar = document.createElement('div');
                bar.classList.add('bar');
                bar.style.height = value + 'px';
                arrayContainer.appendChild(bar);
            });
        }

        async function quickSort(arr, low, high) {
            if (low < high) {
                const pi = await partition(arr, low, high);
                await quickSort(arr, low, pi - 1);
                await quickSort(arr, pi + 1, high);
            }
        }

        async function partition(arr, low, high) {
            const pivot = arr[high];
            let i = low - 1;

            for (let j = low; j < high; j++) {
                if (arr[j] < pivot) {
                    i++;
                    [arr[i], arr[j]] = [arr[j], arr[i]];
                    drawArray(arr);
                    await sleep(400); // Pause for animation
                }
            }
            [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]];
            drawArray(arr);
            await sleep(400); // Pause for animation

            return i + 1;
        }

        function sleep(ms) {
            return new Promise(resolve => setTimeout(resolve, ms));
        }
    </script>

