<body>
    <h1>Understanding Priority Queues</h1>
    <div id="input-section">
        <input type="number" id="priority-input" placeholder="Enter priority">
        <button id="add">Add</button>
        <button id="remove">Remove Highest Priority</button>
    </div>
    <div id="queue"></div>
    <div id="message"></div>
    <script>
        const queue = [];
        const queueDiv = document.getElementById('queue');
        const priorityInput = document.getElementById('priority-input');
        const addButton = document.getElementById('add');
        const removeButton = document.getElementById('remove');
        const messageDiv = document.getElementById('message');

        function updateQueueDisplay() {
            queueDiv.innerHTML = '';
            queue.sort((a, b) => b.priority - a.priority);
            queue.forEach((element, index) => {
                const elDiv = document.createElement('div');
                elDiv.className = 'element';
                elDiv.innerText = element.priority;
                queueDiv.appendChild(elDiv);
            });
        }

        addButton.onclick = function() {
            const priorityValue = parseInt(priorityInput.value);
            if (isNaN(priorityValue)) {
                messageDiv.innerText = 'Please enter a valid number.';
                return;
            }
            messageDiv.innerText = '';
            queue.push({ priority: priorityValue });
            priorityInput.value = '';
            updateQueueDisplay();
        };

        removeButton.onclick = function() {
            if (queue.length === 0) {
                messageDiv.innerText = 'Queue is empty. Nothing to remove.';
                return;
            }
            messageDiv.innerText = '';
            const highestPriorityElement = queue.sort((a, b) => b.priority - a.priority)[0];
            const indexToRemove = queue.indexOf(highestPriorityElement);

            const elDiv = document.createElement('div');
            elDiv.className = 'element';
            elDiv.innerText = highestPriorityElement.priority;
            elDiv.style.transform = 'translateX(-100%)';
            queueDiv.appendChild(elDiv);

            setTimeout(() => {
                queue.splice(indexToRemove, 1);
                updateQueueDisplay();
            }, 300);
        };
    </script>
</body>
</html>
```

