            background-color: #28a745;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
        }
        button:hover {
            background-color: #218838;
        }
    </style>
</head>
<body>
    <h1>Breadth-First Search (BFS)</h1>
    <p>Explore how BFS traverses a graph level by level. Click on the nodes to activate the BFS algorithm.</p>
    <div id="graph"></div>
    <button id="startBFS">Start BFS</button>

    <script>
        const graph = document.getElementById('graph');
        const startBFSButton = document.getElementById('startBFS');

        const nodes = [
            { id: 1, x: 100, y: 100, edges: [2, 3] },
            { id: 2, x: 200, y: 50, edges: [1, 4] },
            { id: 3, x: 200, y: 150, edges: [1, 5] },
            { id: 4, x: 300, y: 30, edges: [2] },
            { id: 5, x: 300, y: 120, edges: [3] },
        ];

        nodes.forEach(node => {
            const div = document.createElement('div');
            div.className = 'node';
            div.style.left = `${node.x}px`;
            div.style.top = `${node.y}px`;
            div.innerHTML = node.id;
            div.dataset.id = node.id;
            graph.appendChild(div);
        });

        function bfs(startId) {
            const queue = [startId];
            const visited = new Set();
            visited.add(startId);

            let interval = setInterval(() => {
                if (queue.length === 0) {
                    clearInterval(interval);
                    return;
                }
                const currentId = queue.shift();
                const currentNode = document.querySelector(`.node[data-id='${currentId}']`);
                currentNode.classList.add('visited');

                const currentEdges = nodes.find(node => node.id === currentId).edges;
                currentEdges.forEach(edgeId => {
                    if (!visited.has(edgeId)) {
                        visited.add(edgeId);
                        queue.push(edgeId);
                    }
                });
            }, 1000);
        }

        startBFSButton.addEventListener('click', () => {
            // Reset visual state
            document.querySelectorAll('.node').forEach(node => node.classList.remove('visited'));
            bfs(1); // Start BFS from node with id 1
        });
    </script>
</body>
</html>
```

