            padding: 8px 16px;
            font-size: 16px;
            cursor: pointer;
        }
        #visualization {
            display: flex;
            align-items: flex-end;
            height: 300px;
            width: 100%;
            border: 1px solid #ccc;
            margin-top: 20px;
        }
        .bar {
            width: 20px;
            margin-right: 4px;
            background-color: #4285f4;
            transition: height 0.5s;
        }
        .label {
            text-align: center;
            font-size: 14px;
        }
        #instructions {
            margin-top: 20px;
        }
    </style>
</head>
<body>

<div id="inputContainer">
    <input type="text" id="arrayInput" placeholder="Enter numbers (e.g. 1,3,2,4)">
    <button id="sortButton">Sort</button>
</div>

<div id="visualization"></div>

<div id="instructions">
    <h4>Counting Sort Visualization</h4>
    <p>Input a set of integers to see how Counting Sort works step-by-step!</p>
</div>

<script>
    document.getElementById("sortButton").onclick = function() {
        const input = document.getElementById("arrayInput").value;
        const arr = input.split(',').map(Number);

        // Validate input
        if (arr.some(isNaN)) {
            alert("Please enter only numbers separated by commas.");
            return;
        }

        countingSort(arr);
    };

    function countingSort(arr) {
        const maxEle = Math.max(...arr);
        const count = new Array(maxEle + 1).fill(0);
        let output = new Array(arr.length);

        // Count occurrences
        arr.forEach(num => {
            count[num]++;
        });

        // Update the output array reflecting the counts
        let index = 0;
        for (let i = 0; i < count.length; i++) {
            while (count[i] > 0) {
                output[index++] = i;
                count[i]--;
            }
        }

        // Visualize the input
        visualizeArray(arr, "Input Array");
        setTimeout(() => visualizeArray(output, "Sorted Array"), 1000);
    }

    function visualizeArray(arr, title) {
        const visualizationDiv = document.getElementById("visualization");
        visualizationDiv.innerHTML = '';

        arr.forEach(num => {
            const bar = document.createElement('div');
            bar.className = 'bar';
            bar.style.height = num * 10 + 'px'; // Scaling the height
            bar.title = num; // Tooltip to show the number
            visualizationDiv.appendChild(bar);
        });

        const label = document.createElement('div');
        label.className = 'label';
        label.innerText = title;
        visualizationDiv.appendChild(label);
    }
</script>

</body>
</html>
```

