            border: none;
            color: white;
            border-radius: 4px;
            font-size: 16px;
        }
        button:disabled {
            background-color: #bdbdbd;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Heap Sort Interactive Module</h1>
        <div class="objective">
            <strong>Learning Objective:</strong>
            <p>Understand the Heap Sort algorithm through an interactive visualization, where users can visualize heap creation and sorting steps.</p>
        </div>

        <div class="visualization" id="visualization"></div>

        <div class="controls">
            <button id="generate">Generate Array</button>
            <button id="sort" disabled>Sort Array</button>
        </div>
    </div>

    <script>
        const visualization = document.getElementById('visualization');
        const generateButton = document.getElementById('generate');
        const sortButton = document.getElementById('sort');
        let array = [];

        function generateArray() {
            array = [];
            visualization.innerHTML = '';
            for (let i = 0; i < 10; i++) {
                const value = Math.floor(Math.random() * 100) + 1;
                array.push(value);
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = value * 2 + 'px';
                visualization.appendChild(bar);
            }
            sortButton.disabled = false;
        }

        function heapify(arr, n, i) {
            let largest = i;
            const left = 2 * i + 1;
            const right = 2 * i + 2;

            if (left < n && arr[left] > arr[largest]) {
                largest = left;
            }

            if (right < n && arr[right] > arr[largest]) {
                largest = right;
            }

            if (largest !== i) {
                [arr[i], arr[largest]] = [arr[largest], arr[i]];
                visualizeBars(arr);
                heapify(arr, n, largest);
            }
        }

        function heapSort() {
            const n = array.length;
            for (let i = Math.floor(n / 2) - 1; i >= 0; i--) {
                heapify(array, n, i);
            }

            for (let i = n - 1; i > 0; i--) {
                [array[0], array[i]] = [array[i], array[0]];
                visualizeBars(array, true);
                heapify(array, i, 0);
            }
        }

        function visualizeBars(arr, isSorting = false) {
            visualization.innerHTML = '';
            arr.forEach(value => {
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = value * 2 + 'px';
                visualization.appendChild(bar);
            });
            if (isSorting) {
                setTimeout(heapSort, 1000);
            }
        }

        generateButton.onclick = function () {
            generateArray();
        };

        sortButton.onclick = function () {
            sortButton.disabled = true;
            heapSort();
        };
    </script>
</body>
</html>
```

