            background-color: #0056b3;
        }

        #result {
            font-size: 1.2em;
            margin-top: 20px;
            font-weight: bold;
        }
    </style>
</head>
<body>

    <h1>Interactive Linear Search</h1>
    <div id="objective">Understand how Linear Search works by interacting with the array.</div>

    <div id="array-container"></div>

    <div id="input-container">
        <input type="text" id="search-input" placeholder="Enter a number" />
        <button id="search-button">Search</button>
    </div>

    <div id="result"></div>

    <script>
        const array = [5, 3, 8, 6, 2, 7, 4, 1];
        const arrayContainer = document.getElementById("array-container");
        const searchInput = document.getElementById("search-input");
        const searchButton = document.getElementById("search-button");
        const resultText = document.getElementById("result");

        function displayArray() {
            arrayContainer.innerHTML = "";
            array.forEach((value, index) => {
                const element = document.createElement("div");
                element.className = "array-element";
                element.textContent = value;
                element.setAttribute("data-index", index);
                arrayContainer.appendChild(element);
            });
        }

        async function linearSearch(target) {
            resultText.textContent = "";
            const elements = document.querySelectorAll(".array-element");
            for (let i = 0; i < array.length; i++) {
                elements[i].classList.add("searching");
                await new Promise(resolve => setTimeout(resolve, 500)); // Pause for 500ms
                if (array[i] === target) {
                    elements[i].classList.remove("searching");
                    elements[i].classList.add("found");
                    resultText.textContent = `Found ${target} at index ${i}!`;
                    return;
                }
                elements[i].classList.remove("searching");
            }
            resultText.textContent = `${target} not found.`;
        }

        searchButton.onclick = () => {
            const target = parseInt(searchInput.value);
            if (!isNaN(target)) {
                linearSearch(target);
            } else {
                resultText.textContent = "Please enter a valid number.";
            }
        };

        // Initial display
        displayArray();
    </script>
</body>
