        <h1>Understanding Insertion Sort</h1>
        <p>Click "Sort" to visualize the Insertion Sort algorithm.</p>
    </header>

    <div id="sort-area"></div>

    <div>
        <button id="sortBtn">Sort</button>
        <button id="shuffleBtn">Shuffle</button>
    </div>

    <script>
        const sortArea = document.getElementById("sort-area");
        const sortBtn = document.getElementById("sortBtn");
        const shuffleBtn = document.getElementById("shuffleBtn");

        let array = [5, 2, 9, 1, 5, 6];
        const drawArray = () => {
            sortArea.innerHTML = "";
            array.forEach((num) => {
                const block = document.createElement("div");
                block.className = "block";
                block.style.height = `${num * 20}px`;
                block.innerText = num;
                sortArea.appendChild(block);
            });
        };

        const insertionSort = async () => {
            for (let i = 1; i < array.length; i++) {
                let key = array[i];
                let j = i - 1;

                while (j >= 0 && array[j] > key) {
                    array[j + 1] = array[j];
                    drawArray();
                    sortArea.children[j].classList.add("highlight");
                    await new Promise((resolve) => setTimeout(resolve, 500));
                    sortArea.children[j].classList.remove("highlight");
                    j--;
                }
                array[j + 1] = key;
                drawArray();
                sortArea.children[j + 1].classList.add("sorted");
            }
        };

        sortBtn.addEventListener("click", insertionSort);
        shuffleBtn.addEventListener("click", () => {
            array = Array.from({ length: 6 }, () => Math.floor(Math.random() * 10) + 1);
            drawArray();
        });

        // Initial draw
        drawArray();
    </script>
</body>

</html>
```

