<body>
    <canvas id="canvas" width="600" height="400"></canvas>
    <div id="controls">
        <div id="instructions">
            <p>Click to add nodes. Drag from one node to another to create edges.</p>
            <p>Click "Run Algorithm" to visualize Kruskal's Algorithm.</p>
        </div>
        <button id="addNode">Add Node</button>
        <button id="addEdge">Add Edge</button>
        <button id="runAlgorithm">Run Algorithm</button>
        <button id="pause">Pause</button>
        <button id="reset">Reset</button>
        <div id="result"></div>
    </div>
    <script>
        // Variables for canvas, nodes, edges, and interaction
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext('2d');
        let nodes = [];
        let edges = [];
        let edgeWeights = {};

        // Function to add nodes
        document.getElementById('addNode').onclick = function() {
            const node = { x: Math.random() * canvas.width, y: Math.random() * canvas.height };
            nodes.push(node);
            draw();
        };

        // Function to add edges
        let drawingEdge = false;
        let startNode = null;

        document.getElementById('canvas').onmousedown = function(event) {
            const mousePos = getMousePos(canvas, event);
            if (!drawingEdge) {
                startNode = findClosestNode(mousePos);
                drawingEdge = true;
            } else {
                const endNode = findClosestNode(mousePos);
                const weight = prompt("Enter weight for this edge:");
                if (startNode !== endNode && weight) {
                    edges.push({ startNode, endNode });
                    edgeWeights[`${startNode}-${endNode}`] = weight;
                }
                drawingEdge = false;
                draw();
            }
        };

        // Function to run the algorithm
        document.getElementById('runAlgorithm').onclick = function() {
            // The algorithm and animation visualization goes here
            // Use edgeWeights and edges to reflect the progress
        };

        // Additional utility functions for the algorithm and drawing
        function getMousePos(canvas, evt) {
            const rect = canvas.getBoundingClientRect();
            return {
                x: evt.clientX - rect.left,
                y: evt.clientY - rect.top
            };
        }

        function findClosestNode(mousePos) {
            let closestNode = null;
            let closestDistance = Infinity;
            nodes.forEach((node, index) => {
                const dist = Math.sqrt(Math.pow(node.x - mousePos.x, 2) + Math.pow(node.y - mousePos.y, 2));
                if (dist < closestDistance) {
                    closestDistance = dist;
                    closestNode = index;
                }
            });
            return closestNode;
        }

        function draw() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            nodes.forEach((node) => {
                ctx.beginPath();
                ctx.arc(node.x, node.y, 5, 0, Math.PI * 2);
                ctx.fillStyle = '#000';
                ctx.fill();
            });
            edges.forEach((edge) => {
                ctx.beginPath();
                ctx.moveTo(nodes[edge.startNode].x, nodes[edge.startNode].y);
                ctx.lineTo(nodes[edge.endNode].x, nodes[edge.endNode].y);
                ctx.strokeStyle = '#000';
                ctx.stroke();
            });
        }

        // Reset button logic
        document.getElementById('reset').onclick = function() {
            nodes = [];
            edges = [];
            edgeWeights = {};
            draw();
        };
    </script>
</body>
</html>
