        const resetBtn = document.getElementById('resetBtn');
        const runBtn = document.getElementById('runBtn');

        const rows = 5;
        const cols = 5;
        let startNode = null;
        let endNode = null;

        // Initialize the grid
        for (let i = 0; i < rows; i++) {
            for (let j = 0; j < cols; j++) {
                const node = document.createElement('div');
                node.classList.add('node');
                node.dataset.row = i;
                node.dataset.col = j;
                node.onclick = handleNodeClick;
                gridElement.appendChild(node);
            }
        }

        // Handle node click
        function handleNodeClick(event) {
            const node = event.target;
            if (!startNode) {
                startNode = node;
                node.classList.add('start');
                return;
            }
            if (!endNode && node !== startNode) {
                endNode = node;
                node.classList.add('end');
                return;
            }
            if (node === startNode) {
                startNode.classList.remove('start');
                startNode = null;
            }
            if (node === endNode) {
                endNode.classList.remove('end');
                endNode = null;
            }
        }

        // Reset the grid
        resetBtn.onclick = () => {
            startNode = null;
            endNode = null;
            document.querySelectorAll('.node').forEach(node => {
                node.classList.remove('start', 'end', 'path');
            });
        };

        // Find the shortest path
        runBtn.onclick = () => {
            if (!startNode || !endNode) {
                alert("Please select start and end nodes.");
                return;
            }
            findShortestPath(startNode, endNode);
        };

        function findShortestPath(start, end) {
            // Simple implementation for path visualization
            // Here we would have the actual Dijkstra's Algorithm logic

            // For demonstration, let's just simulate a path
            const path = [
                {row: 0, col: 0},
                {row: 1, col: 1},
                {row: 2, col: 2},
                {row: 3, col: 3},
                {row: 4, col: 4}
            ];

            path.forEach(coord => {
                const node = document.querySelector(`.node[data-row="${coord.row}"][data-col="${coord.col}"]`);
                if (node) {
                    node.classList.add('path');
                }
            });
        }
    </script>
</body>
</html>
