    </style>
</head>
<body>

    <h1>Breadth-First Search (BFS)</h1>
    <div class="instructions">
        <p>Click on the nodes to create a graph.</p>
        <p>Click "Start BFS" to visualize the search process.</p>
    </div>

    <div id="bfsContainer">
        <div id="graph"></div>
        <div id="controls">
            <button id="startButton">Start BFS</button>
            <button id="clearButton">Clear Graph</button>
        </div>
    </div>

    <script>
        const graphContainer = document.getElementById('graph');
        const startButton = document.getElementById('startButton');
        const clearButton = document.getElementById('clearButton');
        const nodes = []; // Store nodes

        // Create a 5x5 grid of nodes
        for (let i = 0; i < 25; i++) {
            const node = document.createElement('div');
            node.classList.add('node');
            node.innerText = i + 1; // Labeling nodes
            node.addEventListener('click', () => {
                if (!node.classList.contains('visited')) {
                    nodes.push(node);
                    node.style.borderColor = '#2196F3';
                    node.style.backgroundColor = '#e3f2fd';
                }
            });
            graphContainer.appendChild(node);
        }

        // BFS Implementation
        function bfs(startNode) {
            const queue = [];

            // Start from the clicked node
            queue.push(startNode);
            startNode.classList.add('queue');

            const interval = setInterval(() => {
                if (queue.length === 0) {
                    clearInterval(interval);
                    return;
                }
                const currentNode = queue.shift();

                if (!currentNode.classList.contains('visited')) {
                    currentNode.classList.add('visited');
                    currentNode.classList.remove('queue');
                }

                const idx = nodes.indexOf(currentNode);
                const neighbors = [
                    nodes[idx - 1], // left
                    nodes[idx + 1], // right
                    nodes[idx - 5], // top
                    nodes[idx + 5]  // bottom
                ];

                neighbors.forEach(neighbor => {
                    if (neighbor && !neighbor.classList.contains('visited')) {
                        queue.push(neighbor);
                        neighbor.classList.add('queue');
                    }
                });
            }, 1000);
        }

        // Start BFS on button click
        startButton.addEventListener('click', () => {
            if (nodes.length > 0) {
                bfs(nodes[0]); // Start from the first node clicked
            }
        });

        // Clear the graph
        clearButton.addEventListener('click', () => {
            nodes.forEach(node => {
                node.classList.remove('visited', 'queue');
                node.style.borderColor = '#9e9e9e';
                node.style.backgroundColor = '#ffffff';
            });
            nodes.length = 0; // Clear the array
        });
    </script>

</body>
</html>
