        let totalWeight = 0;
        const selectedItems = [];

        itemCards.forEach(card => {
            if (card.querySelector('input').checked) {
                const value = parseInt(card.dataset.value);
                const weight = parseInt(card.dataset.weight);
                if (totalWeight + weight <= capacity) {
                    totalWeight += weight;
                    totalValue += value;
                    selectedItems.push(card);
                }
            }
        });

        resultDiv.innerHTML = `Total Value: ${totalValue}, Total Weight: ${totalWeight}`;
        resetCards(); // Reset styles before highlighting new selections

        selectedItems.forEach(card => {
            card.classList.add('selected'); // Visually show selected items
        });
