<body>
    <div class="container">
        <h1>Understanding Recursion</h1>
        <div class="objective">
            Learn how recursion works by calculating the factorial of a number through an interactive process.
        </div>
        <div class="input-area">
            <input type="number" id="inputNumber" class="input-box" placeholder="Enter a number" min="0" />
            <button class="button" id="calculateButton">Calculate Factorial</button>
        </div>
        <div class="loading">Calculating...</div>
        <div class="output-area" id="outputArea">
            <div class="output-message" id="outputMessage"></div>
        </div>
    </div>

    <script>
        document.getElementById("calculateButton").addEventListener("click", function() {
            const number = parseInt(document.getElementById("inputNumber").value);
            const outputMessage = document.getElementById("outputMessage");
            const loadingMessage = document.querySelector('.loading');
            const outputArea = document.getElementById("outputArea");

            if (!number >= 0) {
                outputMessage.textContent = "Please enter a non-negative integer.";
                outputArea.style.height = 'auto';
                return;
            }

            loadingMessage.style.display = "block";
            outputArea.style.height = '0';
            setTimeout(() => {
                const result = factorial(number);
                loadingMessage.style.display = "none";
                outputMessage.textContent = `The factorial of ${number} is ${result}.`;
                outputArea.style.height = 'auto';
            }, 500); // Simulate processing delay
        });

        function factorial(n) {
            if (n === 0 || n === 1) {
                return 1; // Base case
            } 
            return n * factorial(n - 1); // Recursive case
        }
    </script>
</body>
</html>
