    <div id="item-list">
        <div class="item" data-weight="4" data-value="10">Item 1: (Weight: 4, Value: 10) <input type="checkbox"></div>
        <div class="item" data-weight="2" data-value="4">Item 2: (Weight: 2, Value: 4) <input type="checkbox"></div>
        <div class="item" data-weight="5" data-value="7">Item 3: (Weight: 5, Value: 7) <input type="checkbox"></div>
        <div class="item" data-weight="1" data-value="2">Item 4: (Weight: 1, Value: 2) <input type="checkbox"></div>
    </div>

