    <div id="module-container">
        <h1>Interactive Linear Regression</h1>
        <h2>Learning Objective</h2>
        <p>Understand how changing point values affects the linear regression line.</p>

        <div id="canvas"></div>
        <div class="control-panel">
            <input type="number" id="pointX" placeholder="X-coordinate" />
            <input type="number" id="pointY" placeholder="Y-coordinate" />
            <button id="add-point">Add Point</button>
            <button id="reset">Reset</button>
        </div>

        <h2>Gradient Descent Animation</h2>
        <p>Observe how the linear regression line adjusts to best fit the selected points.</p>
    </div>

    <script>
        const canvas = document.getElementById('canvas');
        const pointsContainer = document.createElement('div');
        pointsContainer.id = 'points';
        canvas.appendChild(pointsContainer);

        const pointX = document.getElementById('pointX');
        const pointY = document.getElementById('pointY');
        const addPointButton = document.getElementById('add-point');
        const resetButton = document.getElementById('reset');

        let points = [];

        function drawPoints() {
            pointsContainer.innerHTML = '';
            points.forEach((point, index) => {
                const pointDiv = document.createElement('div');
                pointDiv.style.position = 'absolute';
                pointDiv.style.width = '10px';
                pointDiv.style.height = '10px';
                pointDiv.style.background = 'red';
                pointDiv.style.borderRadius = '50%';
                pointDiv.style.left = `${point.x}px`;
                pointDiv.style.top = `${canvas.clientHeight - point.y}px`;
                pointsContainer.appendChild(pointDiv);
            });
        }

        function drawLine() {
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.clientWidth, canvas.clientHeight);
            points.forEach((point) => {
                ctx.fillStyle = 'red';
                ctx.fillRect(point.x - 5, canvas.clientHeight - point.y - 5, 10, 10);
            });

            if (points.length > 1) {
                const [xSum, ySum, x2Sum] = points.reduce((sums, point) => {
                    sums[0] += point.x;
                    sums[1] += point.y;
                    sums[2] += point.x * point.x;
                    return sums;
                }, [0, 0, 0]);

                const n = points.length;
                const slope = (n * xSum * ySum - xSum * ySum) / (n * x2Sum - xSum * xSum);
                const intercept = (ySum - slope * xSum) / n;

                const x1 = 0;
                const y1 = intercept;
                const x2 = canvas.clientWidth;
                const y2 = slope * x2 + intercept;

                ctx.beginPath();
                ctx.moveTo(x1, canvas.clientHeight - y1);
                ctx.lineTo(x2, canvas.clientHeight - y2);
                ctx.strokeStyle = 'blue';
                ctx.lineWidth = 2;
                ctx.stroke();
            }
        }

        addPointButton.addEventListener('click', () => {
            const xValue = parseInt(pointX.value);
            const yValue = parseInt(pointY.value);
            if (!isNaN(xValue) && !isNaN(yValue) && xValue >= 0 && xValue <= canvas.clientWidth && yValue >= 0 && yValue <= canvas.clientHeight) {
                points.push({ x: xValue, y: yValue });
                drawPoints();
                drawLine();
                pointX.value = '';
                pointY.value = '';
            } else {
                alert('Please enter valid coordinates!');
            }
        });

        resetButton.addEventListener('click', () => {
            points = [];
            drawPoints();
            drawLine();
            pointX.value = '';
            pointY.value = '';
        });
    </script>
</body>
