                const weight = parseInt(this.getAttribute('data-weight'));
                const value = parseInt(this.getAttribute('data-value'));

                this.classList.toggle('selected');

                if (this.classList.contains('selected')) {
                    totalWeight += weight;
                    totalValue += value;
                    selectedItemsDiv.innerHTML += `<div class="knapsack-item">${this.textContent}</div>`;
                } else {
                    totalWeight -= weight;
                    totalValue -= value;
                    const items = selectedItemsDiv.children;
                    [...items].forEach((el, index) => {
                        if (el.textContent === this.textContent) {
                            selectedItemsDiv.removeChild(items[index]);
                        }
                    });
                }
                updateTotals();
            });
        });

