            maxWeightInput.textContent = this.value;
            if (totalWeight > this.value) {
                alert('Total weight exceeds maximum weight!');
            }
        });

        function updateTotals() {
            totalWeightSpan.textContent = totalWeight;
            totalValueSpan.textContent = totalValue;
        }

        function resetSelection() {
            totalWeight = 0;
            totalValue = 0;
            selectedItemsDiv.innerHTML = '';
            updateTotals();
            document.querySelectorAll('.item').forEach(item => {
                item.classList.remove('selected');
            });
        }
    </script>

