            background: #218838; 
        }
        .frequency-table, .huffman-tree {
            margin-top: 24px; 
            display: none;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Understanding Huffman Coding</h1>
        <p>Input a string of characters to explore Huffman coding and its tree structure.</p>
        <div class="controls">
            <input type="text" id="inputString" placeholder="Enter a string..." />
            <button id="generateButton">Generate Huffman Tree</button>
        </div>
        <div class="frequency-table" id="frequencyDisplay"></div>
        <div class="huffman-tree" id="huffmanTreeDisplay"></div>
    </div>

    <script>
        document.getElementById('generateButton').onclick = function() {
            const input = document.getElementById('inputString').value;
            if (!input) {
                alert("Please enter a valid string.");
                return;
            }
            const frequency = getFrequency(input);
            displayFrequency(frequency);
            const huffmanTree = buildHuffmanTree(frequency);
            displayHuffmanTree(huffmanTree);
        }

        function getFrequency(input) {
            const frequency = {};
            for (const char of input) {
                frequency[char] = (frequency[char] || 0) + 1;
            }
            return frequency;
        }

        function displayFrequency(frequency) {
            const frequencyDisplay = document.getElementById('frequencyDisplay');
            frequencyDisplay.innerHTML = "<h2>Character Frequencies:</h2><ul>" + 
                Object.entries(frequency).map(([char, count]) => `<li>${char}: ${count}</li>`).join("") + 
                "</ul>";
            frequencyDisplay.style.display = "block";
        }

        function buildHuffmanTree(frequency) {
            // Implementation of Huffman Tree construction (Pseudocode-style for clarity)
            let heap = Object.entries(frequency).map(([char, freq]) => ({ char, freq }));
            heap.sort((a, b) => a.freq - b.freq);
            while (heap.length > 1) {
                const left = heap.shift();
                const right = heap.shift();
                const newNode = { char: null, freq: left.freq + right.freq, left, right };
                heap.push(newNode);
                heap.sort((a, b) => a.freq - b.freq);
            }
            return heap[0];
        }

        function displayHuffmanTree(node) {
            const huffmanTreeDisplay = document.getElementById('huffmanTreeDisplay');
            huffmanTreeDisplay.innerHTML = "<h2>Huffman Tree:</h2>" + JSON.stringify(node, null, 2);
            huffmanTreeDisplay.style.display = "block";
        }
    </script>
</body>
</html>
```

