            background-color: #0056b3;
        }
        #output {
            text-align: center;
            font-size: 18px;
            margin-top: 16px;
        }
    </style>
</head>
<body>

<div class="container">
    <h1>Explore Prim's Algorithm</h1>
    <p align="center"><strong>Learning Objective:</strong> Understand how Prim's Algorithm constructs a minimum spanning tree by clicking to add nodes and edges.</p>
    <div id="graph-container">
        <svg id="viewport"></svg>
    </div>
    <div id="controls">
        <button id="startButton">Start Algorithm</button>
        <button id="resetButton">Reset</button>
    </div>
    <div id="output"></div>
</div>

<script>
    const viewport = document.getElementById("viewport");
    const startButton = document.getElementById("startButton");
    const resetButton = document.getElementById("resetButton");
    const output = document.getElementById("output");

    let nodes = [];
    let edges = [];
    let selectedNode = null;
    let adjacencyMatrix = [];

    viewport.addEventListener("click", (event) => {
        const rect = viewport.getBoundingClientRect();
        const x = event.clientX - rect.left;
        const y = event.clientY - rect.top;

        addNode(x, y);
        drawGraph();
    });

    function addNode(x, y) {
        const nodeIndex = nodes.length;
        nodes.push({ x, y, id: nodeIndex });
        adjacencyMatrix.push(new Array(nodeIndex + 1).fill(Infinity));
        adjacencyMatrix[nodeIndex][nodeIndex] = 0;
    }

    function addEdge(node1, node2, weight) {
        edges.push({node1, node2, weight});
        adjacencyMatrix[node1.id][node2.id] = weight;
        adjacencyMatrix[node2.id][node1.id] = weight;
    }

    function drawGraph() {
        viewport.innerHTML = '';
        nodes.forEach((node, index) => {
            const nodeElement = document.createElementNS("http://www.w3.org/2000/svg", "circle");
            nodeElement.setAttribute("class", "node");
            nodeElement.setAttribute("cx", node.x);
            nodeElement.setAttribute("cy", node.y);
            nodeElement.setAttribute("r", 15);
            nodeElement.textContent = node.id;

            viewport.appendChild(nodeElement);
            nodeElement.addEventListener("click", (e) => {
                e.stopPropagation();
                selectedNode = node;
                nodeElement.classList.add("selected");
                addEdges();
            });
        });

        edges.forEach(edge => {
            const edgeElement = document.createElementNS("http://www.w3.org/2000/svg", "line");
            edgeElement.setAttribute("class", "edge");
            edgeElement.setAttribute("x1", edge.node1.x);
            edgeElement.setAttribute("y1", edge.node1.y);
            edgeElement.setAttribute("x2", edge.node2.x);
            edgeElement.setAttribute("y2", edge.node2.y);
            viewport.appendChild(edgeElement);
        });
    }

    function addEdges() {
        if (selectedNode) {
            nodes.forEach(node => {
                if (node !== selectedNode) {
                    const weight = 1 + Math.floor(Math.random() * 10); // Random weight between 1 to 10
                    addEdge(selectedNode, node, weight);
                }
            });
            drawGraph();
            output.textContent = "Edges added from node " + selectedNode.id;
        }
    }

    function startAlgorithm() {
        output.textContent = "Executing Prim's Algorithm...";
        resetColors();
        const mst = [];
        const visited = Array(nodes.length).fill(false);
        visited[0] = true;

        while (mst.length < nodes.length - 1) {
            let minEdge = {weight: Infinity};
            for (let i = 0; i < nodes.length; i++) {
                if (visited[i]) {
                    for (let j = 0; j < nodes.length; j++) {
                        if (!visited[j] && adjacencyMatrix[i][j] < minEdge.weight) {
                            minEdge = {node1: nodes[i], node2: nodes[j], weight: adjacencyMatrix[i][j]};
                        }
                    }
                }
            }

            if (minEdge.weight !== Infinity) {
                mst.push(minEdge);
                visited[minEdge.node2.id] = true;
                highlightEdge(minEdge.node1, minEdge.node2);
                output.textContent = "Edge added: " + minEdge.node1.id + " - " + minEdge.node2.id + " (Weight: " + minEdge.weight + ")";
            }
        }
    }

    function highlightEdge(node1, node2) {
        const edgeElement = document.createElementNS("http://www.w3.org/2000/svg", "line");
        edgeElement.setAttribute("class", "edge highlight");
        edgeElement.setAttribute("x1", node1.x);
        edgeElement.setAttribute("y1", node1.y);
        edgeElement.setAttribute("x2", node2.x);
        edgeElement.setAttribute("y2", node2.y);
        viewport.appendChild(edgeElement);
    }

    function resetColors() {
        const selected = document.querySelectorAll('.node.selected');
        selected.forEach(node => node.classList.remove('selected'));
    }

    startButton.addEventListener("click", startAlgorithm);
    resetButton.addEventListener("click", () => {
        nodes = [];
        edges = [];
        selectedNode = null;
        adjacencyMatrix = [];
        viewport.innerHTML = '';
        output.textContent = '';
    });
</script>

</body>
</html>
```

