    <div class="container" id="array-container">
        <!-- Array elements will be generated here -->
    </div>
    <button class="button" id="start-button">Start Divide and Conquer!</button>

    <script>
        const arrayContainer = document.getElementById('array-container');
        const startButton = document.getElementById('start-button');
        const arraySize = 8; // Size of the array to visualize
        let array = [];

        function generateRandomArray(size) {
            return Array.from({ length: size }, () => Math.floor(Math.random() * 100));
        }

        function createArrayElements(arr) {
            arrayContainer.innerHTML = '';
            arr.forEach(num => {
                const box = document.createElement('div');
                box.className = 'array-box';
                box.textContent = num;
                arrayContainer.appendChild(box);
            });
        }

        function divideAndConquer(arr) {
            if (arr.length <= 1) {
                return arr;
            }

            const mid = Math.floor(arr.length / 2);
            const left = divideAndConquer(arr.slice(0, mid));
            const right = divideAndConquer(arr.slice(mid));

            // Visual feedback
            setTimeout(() => {
                createArrayElements(arr); // Visualize the divided array
                const leftSubArray = arr.slice(0, mid);
                const rightSubArray = arr.slice(mid);
                animateSplit(leftSubArray, rightSubArray);
            }, 500);
            return [...left, ...right];
        }

        function animateSplit(left, right) {
            const boxes = document.querySelectorAll('.array-box');
            boxes.forEach((box, index) => {
                if (index < left.length) {
                    box.style.transform = 'translateY(-30px)';
                } else {
                    box.style.transform = 'translateY(30px)';
                }
            });
            // Reset boxes after animation
            setTimeout(() => {
                boxes.forEach(box => box.style.transform = 'translateY(0)');
            }, 1000);
        }

        startButton.addEventListener('click', function() {
            startButton.disabled = true;
            array = generateRandomArray(arraySize);
            createArrayElements(array);
            divideAndConquer(array);
            startButton.textContent = "Visualizing...";
        });
    </script>

</body>
