        <button id="traverseListBtn">Traverse List</button>
        <button id="clearListBtn">Clear List</button>
    </div>

    <script>
        let head = null;
        let selectedNode = null;

        function createNode(value) {
            const node = document.createElement('div');
            node.classList.add('node');
            node.textContent = value;
            node.onclick = function() {
                if (selectedNode) selectedNode.classList.remove('selected');
                selectedNode = node;
                node.classList.add('selected');
            };
            return node;
        }

        document.getElementById('addNodeBtn').onclick = function() {
            const value = document.getElementById('nodeValue').value;
            if (value) {
                const node = createNode(value);
                document.getElementById('listContainer').appendChild(node);

                if (head === null) {
                    head = node;
                } else {
                    const line = document.createElement('div');
                    line.classList.add('line');
                    node.style.marginLeft = "50px"; // Space between nodes
                    document.getElementById('listContainer').appendChild(line);
                }
                document.getElementById('nodeValue').value = '';
            }
        };

        document.getElementById('deleteNodeBtn').onclick = function() {
            if (selectedNode) {
                selectedNode.remove();
                selectedNode = null;
            }
        };

        document.getElementById('traverseListBtn').onclick = function() {
            const nodes = document.querySelectorAll('.node');
            let index = 0;

            const interval = setInterval(() => {
                if (index > 0) {
                    nodes[index - 1].style.opacity = 1; // Fade out previous
                }
                if (index < nodes.length) {
                    nodes[index].style.opacity = 0.5; // Highlight current
                    index++;
                } else {
                    clearInterval(interval);
                }
            }, 1000);
        };

        document.getElementById('clearListBtn').onclick = function() {
            document.getElementById('listContainer').innerHTML = '';
            head = null;
            selectedNode = null;
        };
    </script>
</body>
</html>
