    <h1>Understanding Bubble Sort</h1>
    <p>Click "Start Sorting" to visualize the Bubble Sort algorithm.</p>
    <div id="array-container"></div>
    <button id="start-btn">Start Sorting</button>
    <button id="reset-btn">Reset</button>

    <script>
        const arrayContainer = document.getElementById('array-container');
        const startBtn = document.getElementById('start-btn');
        const resetBtn = document.getElementById('reset-btn');
        let arr = [];
        const arraySize = 10;

        // Generate random array and render
        function generateArray() {
            arr = Array.from({ length: arraySize }, () => Math.floor(Math.random() * 100) + 1);
            renderArray();
        }

        // Render the array in the container
        function renderArray() {
            arrayContainer.innerHTML = '';
            arr.forEach(value => {
                const bar = document.createElement('div');
                bar.classList.add('bar');
                bar.style.height = `${value * 2}px`; // Scale height for visibility
                bar.style.width = '24px';
                arrayContainer.appendChild(bar);
            });
        }

        // Bubble sort algorithm visualization
        async function bubbleSort() {
            const bars = document.querySelectorAll('.bar');
            let sorted = false;
            let n = arr.length;

            while (!sorted) {
                sorted = true;
                for (let i = 0; i < n - 1; i++) {
                    // Highlight current bars
                    bars[i].classList.add('active');
                    bars[i + 1].classList.add('active');

                    await new Promise(resolve => setTimeout(resolve, 500)); // Wait for 500ms

                    if (arr[i] > arr[i + 1]) {
                        // Swap elements
                        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
                        renderArray();
                        sorted = false;
                    }

                    // Reset highlight
                    bars[i].classList.remove('active');
                    bars[i + 1].classList.remove('active');
                }
                n--; // Reduce the size for optimization
            }

            // Indicate sorted bars
            bars.forEach(bar => bar.classList.add('sorted'));
        }

        // Event Listeners
        startBtn.addEventListener('click', () => {
            bubbleSort();
        });

        resetBtn.addEventListener('click', () => {
            generateArray();
        });

        // Initialize the array
        generateArray();
    </script>
</body>

