### JavaScript Functionality (script.js)

```javascript
class Node {
    constructor(value) {
        this.value = value;
        this.left = null;
        this.right = null;
    }
}

class BinarySearchTree {
    constructor() {
        this.root = null;
    }

    insert(value) {
        const newNode = new Node(value);
        if (!this.root) {
            this.root = newNode;
            return this;
        }
        let current = this.root;
        while (true) {
            if (value < current.value) {
                if (!current.left) {
                    current.left = newNode;
                    return this;
                } else {
                    current = current.left;
                }
            } else {
                if (!current.right) {
                    current.right = newNode;
                    return this;
                } else {
                    current = current.right;
                }
            }
        }
    }
}

const bst = new BinarySearchTree();
const insertBtn = document.getElementById('insertBtn');
const nodeValueInput = document.getElementById('nodeValue');
const treeArea = document.getElementById('treeArea');

insertBtn.addEventListener('click', () => {
    const value = parseInt(nodeValueInput.value);
    if (!isNaN(value)) {
        bst.insert(value);
        drawTree(bst.root);
        nodeValueInput.value = ''; // Clear input
    } else {
        alert("Please enter a valid number.");
    }
});

function drawTree(node, x = 400, y = 20, gap = 100) {
    treeArea.innerHTML = ''; // Clear previous drawings
    drawNode(node, x, y, gap);
}

function drawNode(node, x, y, gap) {
    if (node) {
        const div = document.createElement("div");
        div.innerHTML = node.value;
        div.style.position = "absolute";
        div.style.left = `${x}px`;
        div.style.top = `${y}px`;
        div.style.width = "30px";
        div.style.height = "30px";
        div.style.border = "1px solid #000";
        div.style.borderRadius = "50%";
        div.style.backgroundColor = "#ffc107";
        div.style.display = "flex";
        div.style.alignItems = "center";
        div.style.justifyContent = "center";

        treeArea.appendChild(div);

        if (node.left) {
            drawNode(node.left, x - gap, y + 60, gap / 2);
            drawLine(x, y, x - gap, y + 60);
        }
        if (node.right) {
            drawNode(node.right, x + gap, y + 60, gap / 2);
            drawLine(x, y, x + gap, y + 60);
        }
    }
}

function drawLine(x1, y1, x2, y2) {
    const line = document.createElement('div');
    const length = Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2);
    const angle = Math.atan2(y2 - y1, x2 - x1) * 180 / Math.PI;
    line.style.position = 'absolute';
    line.style.width = `${length}px`;
    line.style.transform = `rotate(${angle}deg)`;
    line.style.transformOrigin = '0 0';
    line.style.backgroundColor = '#000';
    line.style.height = '2px';
    line.style.left = `${x1}px`;
    line.style.top = `${y1}px`;
    treeArea.appendChild(line);
}
