<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bubble Sort Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            text-align: center;
            margin: 20px;
        }
        .bar {
            display: inline-block;
            width: 20px;
            margin: 1px;
            background-color: skyblue;
        }
        .swapping {
            background-color: coral;
        }
        button {
            margin-top: 20px;
            padding: 10px 20px;
            font-size: 16px;
        }
    </style>
</head>
<body>

<h1>Bubble Sort Visualization</h1>
<div id="array-container"></div>
<button id="sort-button">Start Sorting</button>

<script>
    const arrayContainer = document.getElementById('array-container');
    const sortButton = document.getElementById('sort-button');

    // Generate an array of random values
    let array = Array.from({ length: 20 }, () => Math.floor(Math.random() * 100));

    function renderArray() {
        arrayContainer.innerHTML = '';
        array.forEach(value => {
            const bar = document.createElement('div');
            bar.style.height = `${value * 2}px`; // Scale the height for better visibility
            bar.classList.add('bar');
            arrayContainer.appendChild(bar);
        });
    }

    async function bubbleSort() {
        for (let i = 0; i < array.length; i++) {
            for (let j = 0; j < array.length - 1 - i; j++) {
                if (array[j] > array[j + 1]) {
                    // Swap the elements
                    const temp = array[j];
                    array[j] = array[j + 1];
                    array[j + 1] = temp;

                    // Update the visualization
                    renderArray();
                    await new Promise(resolve => setTimeout(resolve, 100)); // Pause for a moment
                }
            }
        }
    }

    sortButton.addEventListener('click', () => {
        renderArray(); // Initial render
        bubbleSort();
    });

    // Render the initial array
    renderArray();
</script>

</body>
</html>
