    <div class="container">
        <h1>K-Nearest Neighbors (KNN) Exploration</h1>
        <p>K-Nearest Neighbors (KNN) is a simple yet effective classification algorithm that categorizes data based on the closest training examples in the feature space.</p>

        <div class="plot" id="knnPlot"></div>

        <div class="info">
            <h2>Interactive Parameters</h2>
            <label for="kValue">Select value of K:</label>
            <input type="number" id="kValue" value="3" min="1" max="10">
            <button id="updateButton">Update KNN</button>
        </div>

        <div class="info" id="resultInfo"></div>

        <script>
            const knnPlot = document.getElementById('knnPlot');
            const kValueInput = document.getElementById('kValue');
            const updateButton = document.getElementById('updateButton');
            const resultInfo = document.getElementById('resultInfo');
            let k = parseInt(kValueInput.value);

            function drawPlot(k) {
                // Clear existing canvas
                knnPlot.innerHTML = '';

                // Sample data points
                const dataPoints = [
                    { x: 1, y: 2, label: 'A' },
                    { x: 1, y: 4, label: 'A' },
                    { x: 2, y: 3, label: 'A' },
                    { x: 5, y: 2, label: 'B' },
                    { x: 5, y: 4, label: 'B' },
                    { x: 6, y: 3, label: 'B' },
                    { x: 3, y: 4, label: 'New Point' } // Sample new point to classify
                ];

                const width = knnPlot.clientWidth;
                const height = knnPlot.clientHeight;

                const svg = document.createElementNS("http://www.w3.org/2000/svg", "svg");
                svg.setAttribute("width", width);
                svg.setAttribute("height", height);

                dataPoints.forEach(point => {
                    const circle = document.createElementNS("http://www.w3.org/2000/svg", "circle");
                    circle.setAttribute("cx", point.x * 50);
                    circle.setAttribute("cy", height - point.y * 50);
                    circle.setAttribute("r", 5);
                    circle.setAttribute("fill", point.label === 'New Point' ? 'orange' : (point.label === 'A' ? 'blue' : 'red'));
                    svg.appendChild(circle);
                });

                knnPlot.appendChild(svg);
                classifyPoint(dataPoints, k);
            }

            function classifyPoint(points, k) {
                const newPoint = points.find(p => p.label === 'New Point');

                // Distance calculation
                const distances = points.filter(p => p.label !== 'New Point').map(p => {
                    return {
                        ...p,
                        distance: Math.sqrt(Math.pow(p.x - newPoint.x, 2) + Math.pow(p.y - newPoint.y, 2))
                    };
                });

                // Sort the distances
                distances.sort((a, b) => a.distance - b.distance);

                // Get the k nearest
                const nearest = distances.slice(0, k);

                // Voting
                const votes = {};
                nearest.forEach(n => {
                    votes[n.label] = (votes[n.label] || 0) + 1;
                });

                const classifiedLabel = Object.keys(votes).reduce((a, b) => votes[a] > votes[b] ? a : b);

                resultInfo.innerHTML = `Classified label for new point: <strong>${classifiedLabel}</strong> (K=${k})`;
            }

            updateButton.addEventListener('click', () => {
                k = parseInt(kValueInput.value);
                drawPlot(k);
            });

            // Initial drawing
            drawPlot(k);
        </script>
    </div>
</body>
