    <h1>Bubble Sort Visualization</h1>
    <p>Click "Start" to visualize the Bubble Sort algorithm on the generated array.</p>

    <div id="array-container"></div>

    <div id="controls">
        <button id="generate">Generate New Array</button>
        <button id="start">Start Sorting</button>
        <input type="text" id="array-input" placeholder="Enter numbers separated by commas" />
    </div>

    <script>
        let array = [];
        const arrayContainer = document.getElementById('array-container');

        function generateArray() {
            array = [];
            const input = document.getElementById('array-input').value;
            if (input) {
                array = input.split(',').map(Number);
            } else {
                array = Array.from({length: 10}, () => Math.floor(Math.random() * 100) + 1);
            }
            renderArray();
        }

        function renderArray() {
            arrayContainer.innerHTML = '';
            array.forEach(num => {
                const bar = document.createElement('div');
                bar.classList.add('bar');
                bar.style.height = `${num * 2}px`; // Scale height for better visibility
                bar.style.backgroundColor = '#3498db';
                arrayContainer.appendChild(bar);
            });
        }

        async function bubbleSort() {
            let n = array.length;
            for (let i = 0; i < n - 1; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    renderArrayElement(j, j + 1);
                    if (array[j] > array[j + 1]) {
                        // Swap
                        [array[j], array[j + 1]] = [array[j + 1], array[j]];
                    }
                    await new Promise(resolve => setTimeout(resolve, 200)); // Delay for visualization
                    renderArray();
                }
            }
        }

        function renderArrayElement(index1, index2) {
            const bars = document.querySelectorAll('.bar');
            bars[index1].style.backgroundColor = '#e74c3c'; // Highlight current element
            bars[index2].style.backgroundColor = '#e74c3c'; // Highlight next element
        }

        document.getElementById('generate').addEventListener('click', generateArray);
        document.getElementById('start').addEventListener('click', bubbleSort);

        // On load, generate the initial array
        generateArray();
    </script>

</body>
