                    node.left = this.removeNode(node.left, value);
                    return node;
                } else if (value > node.value) {
                    node.right = this.removeNode(node.right, value);
                    return node;
                } else {
                    if (!node.left && !node.right) {
                        return null;
                    }
                    if (!node.left) {
                        return node.right;
                    }
                    if (!node.right) {
                        return node.left;
                    }
                    const tempNode = this.findMinNode(node.right);
                    node.value = tempNode.value;
                    node.right = this.removeNode(node.right, tempNode.value);
                    return node;
                }
            }

            findMinNode(node) {
                while (node && node.left) {
                    node = node.left;
                }
                return node;
            }

            inOrder(node) {
                let result = [];
                if (node) {
                    result = this.inOrder(node.left);
                    result.push(node.value);
                    result = result.concat(this.inOrder(node.right));
                }
                return result;
            }

            drawTree(node) {
                if (!node) return '';
                return `
                    <div class="node">${node.value}</div>
                    <div style="display: flex; justify-content: center;">
                        <div style="margin-right: 20px;">${this.drawTree(node.left)}</div>
                        <div style="margin-left: 20px;">${this.drawTree(node.right)}</div>
                    </div>
                `;
            }
        }

        const bst = new BinarySearchTree();
        const bstContainer = document.getElementById('bst');

        document.getElementById('insert-btn').addEventListener('click', () => {
            const value = parseInt(document.getElementById('node-value').value);
            if (!isNaN(value)) {
                bst.insert(value);
                bstContainer.innerHTML = bst.drawTree(bst.root);
                document.getElementById('node-value').value = '';
            }
        });

        document.getElementById('remove-btn').addEventListener('click', () => {
            const value = parseInt(document.getElementById('node-value').value);
            if (!isNaN(value)) {
                bst.remove(value);
                bstContainer.innerHTML = bst.drawTree(bst.root);
                document.getElementById('node-value').value = '';
            }
        });
    </script>
</body>

</html>
