<h1>Interactive Linked List Explorer</h1>
<div id="list"></div>

<div id="controls">
    <input type="text" id="nodeValue" placeholder="Node Value">
    <button id="addNode">Add Node</button>
    <button id="removeNode">Remove Tail Node</button>
</div>

<div id="output"></div>

<script>
    class Node {
        constructor(value) {
            this.value = value;
            this.next = null;
        }
    }

    class LinkedList {
        constructor() {
            this.head = null;
        }

        add(value) {
            const newNode = new Node(value);
            if (!this.head) {
                this.head = newNode;
            } else {
                let current = this.head;
                while (current.next) {
                    current = current.next;
                }
                current.next = newNode;
            }
        }

        remove() {
            if (!this.head) return;
            if (!this.head.next) {
                this.head = null;
                return;
            }
            let current = this.head;
            while (current.next.next) {
                current = current.next;
            }
            current.next = null;
        }

        display() {
            const nodes = [];
            let current = this.head;
            while (current) {
                nodes.push(current);
                current = current.next;
            }
            return nodes;
        }
    }

    const linkedList = new LinkedList();

    const renderList = () => {
        const listDiv = document.getElementById('list');
        listDiv.innerHTML = '';
        const nodes = linkedList.display();
        nodes.forEach(node => {
            const nodeDiv = document.createElement('div');
            nodeDiv.className = 'node';
            nodeDiv.innerHTML = `${node.value} <span class="next">â†’</span>`;
            listDiv.appendChild(nodeDiv);
        });
    };

    const addNode = () => {
        const nodeValue = document.getElementById('nodeValue').value;
        if (nodeValue) {
            linkedList.add(nodeValue);
            renderList();
            document.getElementById('nodeValue').value = '';
        }
    };

    const removeNode = () => {
        linkedList.remove();
        renderList();
    };

    document.getElementById('addNode').addEventListener('click', addNode);
    document.getElementById('removeNode').addEventListener('click', removeNode);
</script>

</body>
