                    node.className = 'node';
                    node.style.left = `${(index % levelNodes) * 50}px`;
                    node.style.top = `${Math.floor(index / levelNodes) * nodeHeight}px`;
                    node.innerText = value;
                    visual.appendChild(node);
                });
            }

            getHeapHeight() {
                return Math.ceil(Math.log2(this.heap.length + 1)); 
            }
        }

