<div id="bst-container">
    <div id="bst"></div>
    <div id="controls">
        <input type="number" id="valueInput" placeholder="Enter value" />
        <button id="addButton">Add Node</button>
        <button id="searchButton">Search Node</button>
        <div id="output"></div>
