<script>
    class TreeNode {
        constructor(value) {
            this.value = value;
            this.left = null;
            this.right = null;
        }
    }

    class BinarySearchTree {
        constructor() {
            this.root = null;
        }

        add(value) {
            const newNode = new TreeNode(value);
            if (this.root === null) {
                this.root = newNode;
            } else {
                this.insertNode(this.root, newNode);
            }
        }

        insertNode(node, newNode) {
            if (newNode.value < node.value) {
                if (node.left === null) {
                    node.left = newNode;
                } else {
                    this.insertNode(node.left, newNode);
                }
            } else {
                if (node.right === null) {
                    node.right = newNode;
                } else {
                    this.insertNode(node.right, newNode);
                }
            }
        }

        search(value) {
            return this.searchNode(this.root, value);
        }

        searchNode(node, value) {
            if (node === null) {
                return false;
            }
            if (value < node.value) {
                return this.searchNode(node.left, value);
            } else if (value > node.value) {
                return this.searchNode(node.right, value);
            } else {
                return true;
            }
        }

        draw(node, x, y, gap) {
            if (node) {
                const nodeElement = document.createElement('div');
                nodeElement.className = 'node';
                nodeElement.style.left = `${x}px`;
                nodeElement.style.top = `${y}px`;
                nodeElement.innerText = node.value;
                document.getElementById('bst').appendChild(nodeElement);
                this.draw(node.left, x - gap, y + 50, gap / 2);
                this.draw(node.right, x + gap, y + 50, gap / 2);
            }
        }
    }

    const bst = new BinarySearchTree();

    document.getElementById('addButton').addEventListener('click', () => {
        const value = parseInt(document.getElementById('valueInput').value);
        bst.add(value);
        document.getElementById('bst').innerHTML = '';
        bst.draw(bst.root, 200, 20, 100);
        document.getElementById('output').innerText = `Added: ${value}`;
    });

    document.getElementById('searchButton').addEventListener('click', () => {
        const value = parseInt(document.getElementById('valueInput').value);
        const found = bst.search(value);
        document.getElementById('output').innerText = found ? `Found: ${value}` : `Not Found: ${value}`;
    });
</script>
