        <h2>Stack</h2>
    </div>
</div>

<div class="controls">
    <div class="input-container">
        <input type="text" id="itemInput" placeholder="Enter item to push" />
    </div>
    <button onclick="pushItem()">Push</button>
    <button onclick="popItem()">Pop</button>
    <button onclick="peekItem()">Peek</button>
</div>

<div class="output" id="output"></div>

<script>
    const stack = [];
    const stackElement = document.getElementById('stack');
    const outputElement = document.getElementById('output');

    function updateStackDisplay() {
        stackElement.innerHTML = '<h2>Stack</h2>';
        stack.forEach(item => {
            const itemElement = document.createElement('div');
            itemElement.classList.add('stack-item');
            itemElement.textContent = item;
            stackElement.appendChild(itemElement);
        });
    }

    function pushItem() {
        const itemInput = document.getElementById('itemInput');
        const item = itemInput.value;
        if (item) {
            stack.push(item);
            itemInput.value = '';
            updateStackDisplay();
            outputElement.textContent = `Pushed: ${item}`;
        } else {
            outputElement.textContent = 'Please enter an item.';
        }
    }

    function popItem() {
        if (stack.length === 0) {
            outputElement.textContent = 'Stack is empty. Cannot pop.';
        } else {
            const item = stack.pop();
            updateStackDisplay();
            outputElement.textContent = `Popped: ${item}`;
        }
    }

    function peekItem() {
        if (stack.length === 0) {
            outputElement.textContent = 'Stack is empty. Cannot peek.';
        } else {
            const item = stack[stack.length - 1];
            outputElement.textContent = `Top item: ${item}`;
        }
    }
</script>

</body>
