<body>
    <div class="container">
        <h1>Heap (Min/Max) Interactive Exploration</h1>
        <div class="heap-container" id="heapContainer">
        </div>

        <textarea id="inputArray" placeholder="Enter array elements (comma separated)"></textarea>
        <button onclick="buildHeap()">Build Min/Max Heap</button>

        <div class="info">
            <h2>Heap Operations</h2>
            <p>Select the type of heap you want to build:</p>
            <label>
                <input type="radio" name="heapType" value="min" checked> Min Heap
            </label>
            <label>
                <input type="radio" name="heapType" value="max"> Max Heap
            </label>
            <button onclick="heapify()">Heapify</button>
        </div>
    </div>

    <script>
        let heap = [];

        function buildHeap() {
            const arrayInput = document.getElementById('inputArray').value;
            if (!arrayInput) {
                alert("Please enter valid array elements.");
                return;
            }
            const array = arrayInput.split(',').map(num => parseInt(num.trim())).filter(num => !isNaN(num));
            if (array.length === 0) {
                alert("Array elements must be numbers.");
                return;
            }
            const heapType = document.querySelector('input[name="heapType"]:checked').value;
            heap = buildHeapArray(array, heapType);
            displayHeap();
        }

        function buildHeapArray(array, type) {
            const heap = [];
            array.forEach(item => {
                if (type === "min") {
                    heap.push(item);
                    bubbleUpMin(heap.length - 1, heap);
                } else {
                    heap.push(item);
                    bubbleUpMax(heap.length - 1, heap);
                }
            });
            return heap;
        }

        function bubbleUpMin(index, heap) {
            while (index > 0) {
                const parentIndex = Math.floor((index - 1) / 2);
                if (heap[index] >= heap[parentIndex]) break;
                [heap[index], heap[parentIndex]] = [heap[parentIndex], heap[index]];
                index = parentIndex;
            }
        }

        function bubbleUpMax(index, heap) {
            while (index > 0) {
                const parentIndex = Math.floor((index - 1) / 2);
                if (heap[index] <= heap[parentIndex]) break;
                [heap[index], heap[parentIndex]] = [heap[parentIndex], heap[index]];
                index = parentIndex;
            }
        }

        function displayHeap() {
            const heapContainer = document.getElementById('heapContainer');
            heapContainer.innerHTML = '';
            heap.forEach(value => {
                const node = document.createElement('div');
                node.className = 'node';
                node.textContent = value;
                heapContainer.appendChild(node);
            });
        }

        function heapify() {
            const heapType = document.querySelector('input[name="heapType"]:checked').value;
            const length = heap.length;
            for (let i = Math.floor(length / 2) - 1; i >= 0; i--) {
                if (heapType === "min") {
                    downHeapMin(i, length);
                } else {
                    downHeapMax(i, length);
                }
            }
            displayHeap();
        }

        function downHeapMin(index, length) {
            let smallest = index;
            const left = 2 * index + 1;
            const right = 2 * index + 2;

            if (left < length && heap[left] < heap[smallest]) {
                smallest = left;
            }
            if (right < length && heap[right] < heap[smallest]) {
                smallest = right;
            }
            if (smallest !== index) {
                [heap[index], heap[smallest]] = [heap[smallest], heap[index]];
                downHeapMin(smallest, length);
            }
        }

        function downHeapMax(index, length) {
            let largest = index;
            const left = 2 * index + 1;
            const right = 2 * index + 2;

            if (left < length && heap[left] > heap[largest]) {
                largest = left;
            }
            if (right < length && heap[right] > heap[largest]) {
                largest = right;
            }
            if (largest !== index) {
                [heap[index], heap[largest]] = [heap[largest], heap[index]];
                downHeapMax(largest, length);
            }
        }
    </script>
</body>
</html>
