<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bubble Sort Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            text-align: center;
            background-color: #f4f4f4;
        }
        #array {
            display: flex;
            justify-content: center;
            margin: 20px;
        }
        .bar {
            width: 30px;
            margin: 0 2px;
            background-color: turquoise;
        }
        .sorted {
            background-color: lightgreen;
        }
    </style>
</head>
<body>

    <h1>Bubble Sort Visualization</h1>
    <div id="array"></div>
    <button id="sortButton">Sort Array</button>
    <script>
        let array = [5, 3, 8, 4, 2];
        const arrayContainer = document.getElementById('array');

        function drawArray() {
            arrayContainer.innerHTML = '';
            for (let i = 0; i < array.length; i++) {
                const bar = document.createElement('div');
                bar.style.height = `${array[i] * 20}px`;
                bar.className = 'bar';
                if (i === array.length - 1 && sortedIndex > -1) {
                    bar.classList.add('sorted');
                }
                arrayContainer.appendChild(bar);
            }
        }

        let sortedIndex = -1; // keeps track of the last sorted index

        async function bubbleSort() {
            const length = array.length;
            for (let i = 0; i < length - 1; i++) {
                for (let j = 0; j < length - i - 1; j++) {
                    if (array[j] > array[j + 1]) {
                        // Swap
                        [array[j], array[j + 1]] = [array[j + 1], array[j]];
                    }
                    sortedIndex = length - i - 1; // Update the last sorted index
                    drawArray();
                    await new Promise(resolve => setTimeout(resolve, 500)); // Delay for visualization
                }
            }
        }

        document.getElementById('sortButton').addEventListener('click', () => {
            sortedIndex = -1; // Reset sorted index before sorting
            bubbleSort();
        });

        // Initial drawing of array
        drawArray();
    </script>
</body>
</html>
