    let array = [5, 3, 8, 4, 2];
    const container = document.getElementById('arrayContainer');

    const createBars = () => {
        container.innerHTML = '';
        array.forEach(value => {
            const bar = document.createElement('div');
            bar.style.height = `${value * 20}px`;
            bar.classList.add('bar');
            container.appendChild(bar);
